<?php
include 'db_connect.php';

// Initialize arrays to store pass/fail counts
$pass_count = array_fill(1, 6, 0);
$fail_count = array_fill(1, 6, 0);
$total_students = 0;

// Fetch student marks
$query = "SELECT subject1, subject2, subject3, subject4, subject5, subject6 FROM results";
$result = mysqli_query($conn, $query);

// Process each student's results
while ($row = mysqli_fetch_assoc($result)) {
    $total_students++;
    for ($i = 1; $i <= 6; $i++) {
        if ($row["subject$i"] >= 40) {
            $pass_count[$i]++;
        } else {
            $fail_count[$i]++;
        }
    }
}

mysqli_close($conn);
?>

